# --- PYTORCH ARCHITECTURE (The "Brain") ---
def ConvBlock(in_channels, out_channels, pool=False):
    layers = [nn.Conv2d(in_channels, out_channels, kernel_size=3, padding=1),
             nn.BatchNorm2d(out_channels),
             nn.ReLU(inplace=True)]
    if pool:
        layers.append(nn.MaxPool2d(4))
    return nn.Sequential(*layers)

class ResNet9(nn.Module):
    def __init__(self, in_channels, num_diseases):
        super().__init__()
        self.conv1 = ConvBlock(in_channels, 64)
        self.conv2 = ConvBlock(64, 128, pool=True)
        self.res1 = nn.Sequential(ConvBlock(128, 128), ConvBlock(128, 128))
        self.conv3 = ConvBlock(128, 256, pool=True)
        self.conv4 = ConvBlock(256, 512, pool=True)
        self.res2 = nn.Sequential(ConvBlock(512, 512), ConvBlock(512, 512))
        self.classifier = nn.Sequential(nn.MaxPool2d(4),
                                       nn.Flatten(),
                                       nn.Linear(512, num_diseases))
        
    def forward(self, xb):
        out = self.conv1(xb)
        out = self.conv2(out)
        out = self.res1(out) + out
        out = self.conv3(out)
        out = self.conv4(out)
        out = self.res2(out) + out
        out = self.classifier(out)
        return out

# --- MAGIC TRICK: CACHE THE MODELS ---
@st.cache_resource
def load_models():
    crop_model = joblib.load('RandomForest.pkl')
    
    disease_model = ResNet9(3, 38)
    checkpoint = torch.load('Plant_Disease_Model.pth', map_location=torch.device('cpu'))
    disease_model.load_state_dict(checkpoint)
    disease_model.eval()
    
    return crop_model, disease_model

# Load them into memory
crop_model, disease_model = load_models()

# --- WEATHER API FUNCTION ---
def weather_fetch(city_name):
    """Fetches real-time temperature and humidity from OpenWeatherMap."""
    try:
        # First, try to get the API key from Streamlit's secure cloud vault
        if "weather_api_key" in st.secrets:
            api_key = st.secrets["weather_api_key"]
        else:
            # If not on the cloud, use the local config file
            import config
            api_key = config.weather_api_key
            
        base_url = "http://api.openweathermap.org/data/2.5/weather?"
        complete_url = base_url + "appid=" + api_key + "&q=" + city_name
        response = requests.get(complete_url)
        x = response.json()
        if x["cod"] != "404":
            y = x["main"]
            temperature = round((y["temp"] - 273.15), 2)
            humidity = y["humidity"]
            return temperature, humidity
        else:
            return None
    except Exception as e:
        st.error("‚ö†Ô∏è Could not fetch weather. API key is missing or invalid!")
        return None

