# Convert the raw text into clean Python lists!
indian_locations = {"Manual Entry (Use Sliders)": ["Manual Entry"]}
for state, cities_str in raw_locations.items():
    indian_locations[state] = [city.strip() for city in cities_str.split("|")]


# --- SIDEBAR & NAVIGATION ---
st.sidebar.title("üå± SmartFarm Menu")

if st.sidebar.button("üåó Toggle Light/Dark Mode"):
    st.session_state.theme = "Light" if st.session_state.theme == "Dark" else "Dark"

if st.session_state.theme == "Light":
    st.markdown("""
        <style>
        .stApp { background-color: #F0F2F6; color: #31333F; }
        </style>
    """, unsafe_allow_html=True)
else:
    st.markdown("""
        <style>
        .stApp { background-color: #0E1117; color: #FAFAFA; }
        </style>
    """, unsafe_allow_html=True)

st.sidebar.markdown("---")

app_mode = st.sidebar.radio("Choose a Tool", ["Crop Recommendation", "Fertilizer Advice", "Disease Detection"])

# --- TOOL 1: CROP RECOMMENDATION ---
if app_mode == "Crop Recommendation":
    st.title("üåæ Crop Recommendation AI")
    st.write("Select your State and City to get real-time weather data for the best crop recommendation.")

    # Location Dropdowns powered by the parsed cities.js dictionary!
    col_loc1, col_loc2 = st.columns(2)
    with col_loc1:
        state = st.selectbox("Select State", list(indian_locations.keys()))
    with col_loc2:
        if state == "Manual Entry (Use Sliders)":
            city = None
            st.info("üí° Using manual weather sliders below.")
        else:
            city = st.selectbox("Select City", indian_locations[state])

    st.markdown("---")

    col1, col2 = st.columns(2)
    with col1:
        N = st.number_input("Nitrogen (N)", min_value=0.0, max_value=150.0, value=None, placeholder="e.g. 50")
        P = st.number_input("Phosphorus (P)", min_value=0.0, max_value=150.0, value=None, placeholder="e.g. 50")
        K = st.number_input("Potassium (K)", min_value=0.0, max_value=200.0, value=None, placeholder="e.g. 50")
        
    with col2:
        ph = st.number_input("Soil pH", min_value=0.0, max_value=14.0, value=None, placeholder="e.g. 6.5")
        rainfall = st.number_input("Rainfall (mm)", min_value=0.0, max_value=400.0, value=None, placeholder="e.g. 100")
        temp = st.number_input("Manual Temperature (¬∞C) [Used if Manual Entry selected]", min_value=0.0, max_value=50.0, value=None, placeholder="e.g. 25")
        humidity = st.number_input("Manual Humidity (%) [Used if Manual Entry selected]", min_value=0.0, max_value=100.0, value=None, placeholder="e.g. 70")

    if st.button("Predict Best Crop"):
        if None in [N, P, K, ph, rainfall]:
            st.warning("‚ö†Ô∏è Please fill in all the soil and rainfall details!")
        else:
            final_temp, final_humidity = temp, humidity
            
            if city and city != "Manual Entry":
                with st.spinner(f"Fetching real-time weather for {city}..."):
                    weather_data = weather_fetch(city)
                    if weather_data:
                        final_temp, final_humidity = weather_data
                        st.success(f"üå¶Ô∏è Real-time weather fetched for {city}: {final_temp}¬∞C, {final_humidity}% Humidity")
                    else:
                        st.warning(f"Could not find weather for '{city}'. Please use Manual Entry.")
                        final_temp, final_humidity = None, None

            if final_temp is None or final_humidity is None:
                st.warning("‚ö†Ô∏è Please either select a valid City OR fill in the manual Temperature and Humidity!")
            else:
                features = np.array([[N, P, K, final_temp, final_humidity, ph, rainfall]])
                prediction = crop_model.predict(features)[0]
                st.success(f"üåü We recommend planting: **{prediction.capitalize()}**")


# --- TOOL 2: FERTILIZER ADVICE ---
elif app_mode == "Fertilizer Advice":
    st.title("üßë‚Äçüåæ Fertilizer Advice")
    st.write("Enter your soil nutrients and the crop you want to grow to get custom fertilizer recommendations.")

    try:
        df = pd.read_csv('fertilizer.csv')
        crop_list = df['Crop'].unique()
    except Exception as e:
        st.error("Error loading fertilizer.csv. Make sure the file is in the folder!")
        crop_list = []

    crop_name = st.selectbox("Select the Crop you are growing", crop_list)

    col1, col2, col3 = st.columns(3)
    with col1:
        N = st.number_input("Nitrogen (N)", min_value=0, max_value=150, value=None, placeholder="Enter N", key="f_n")
    with col2:
        P = st.number_input("Phosphorus (P)", min_value=0, max_value=150, value=None, placeholder="Enter P", key="f_p")
    with col3:
        K = st.number_input("Potassium (K)", min_value=0, max_value=200, value=None, placeholder="Enter K", key="f_k")

    if st.button("Get Fertilizer Advice"):
        if None in [N, P, K]:
            st.warning("‚ö†Ô∏è Please enter Nitrogen, Phosphorus, and Potassium values.")
        else:
            nr = df[df['Crop'] == crop_name]['N'].iloc[0]
            pr = df[df['Crop'] == crop_name]['P'].iloc[0]
            kr = df[df['Crop'] == crop_name]['K'].iloc[0]

            n = nr - N
            p = pr - P
            k = kr - K
            
            temp_dict = {abs(n): "N", abs(p): "P", abs(k): "K"}
            max_value = temp_dict[max(temp_dict.keys())]
            
            if max_value == "N":
                key = 'NHigh' if n < 0 else "Nlow"
            elif max_value == "P":
                key = 'PHigh' if p < 0 else "Plow"
            else:
                key = 'KHigh' if k < 0 else "Klow"

            response = fertilizer_dic[key]
            
            # This is the line that fixes the exact issue in your second screenshot!
            # It aggressively removes all HTML and cleans it up for Streamlit.
            clean_response = response.replace('<br/>', '\n\n').replace('<i>', '*').replace('</i>', '*').replace('<b>', '**').replace('</b>', '**').replace('  ', ' ')
            
            st.success("Recommendation Generated!")
            st.info(clean_response)


# --- TOOL 3: DISEASE DETECTION ---
elif app_mode == "Disease Detection":
    st.title("üçÉ Plant Disease Detection")
    st.write("Upload a picture of a plant leaf to detect diseases.")

    uploaded_file = st.file_uploader("Choose an image...", type=["jpg", "jpeg", "png"])

    if uploaded_file is not None:
        image = Image.open(uploaded_file)
        st.image(image, caption="Uploaded Leaf", width=300)

        if st.button("Analyze Leaf"):
            with st.spinner('Analyzing image...'):
                transform = transforms.Compose([
                    transforms.Resize(256),
                    transforms.ToTensor(),
                ])
                img_t = transform(image)
                img_u = torch.unsqueeze(img_t, 0)
                
                classes = [
                    'Apple___Apple_scab', 'Apple___Black_rot', 'Apple___Cedar_apple_rust', 'Apple___healthy',
                    'Blueberry___healthy', 'Cherry_(including_sour)___Powdery_mildew', 'Cherry_(including_sour)___healthy',
                    'Corn_(maize)___Cercospora_leaf_spot Gray_leaf_spot', 'Corn_(maize)___Common_rust_', 'Corn_(maize)___Northern_Leaf_Blight', 'Corn_(maize)___healthy',
                    'Grape___Black_rot', 'Grape___Esca_(Black_Measles)', 'Grape___Leaf_blight_(Isariopsis_Leaf_Spot)', 'Grape___healthy',
                    'Orange___Haunglongbing_(Citrus_greening)', 'Peach___Bacterial_spot', 'Peach___healthy',
                    'Pepper,_bell___Bacterial_spot', 'Pepper,_bell___healthy', 'Potato___Early_blight', 'Potato___Late_blight', 'Potato___healthy',
                    'Raspberry___healthy', 'Soybean___healthy', 'Squash___Powdery_mildew', 'Strawberry___Leaf_scorch', 'Strawberry___healthy',
                    'Tomato___Bacterial_spot', 'Tomato___Early_blight', 'Tomato___Late_blight', 'Tomato___Leaf_Mold', 'Tomato___Septoria_leaf_spot',
                    'Tomato___Spider_mites Two-spotted_spider_mite', 'Tomato___Target_Spot', 'Tomato___Tomato_Yellow_Leaf_Curl_Virus', 'Tomato___Tomato_mosaic_virus', 'Tomato___healthy'
                ]

                with torch.no_grad():
                    prediction = disease_model(img_u)
                    predicted_class = classes[prediction.argmax()]

                desc = disease_dic.get(predicted_class, "No description available.")
                
                # Apply the HTML stripping trick here too!
                clean_desc = desc.replace('<br/>', '\n\n').replace('<i>', '*').replace('</i>', '*').replace('<b>', '**').replace('</b>', '**').replace('  ', ' ')

                st.success(f"### Diagnosis: {predicted_class.replace('___', ' - ').replace('_', ' ')}")
                st.info(clean_desc)